import Vue from 'vue'
import VueRouter from 'vue-router'

import main from '../views/Main.vue'
import App_header from '../layout/AppHeader.vue'
import App_footer from '../layout/AppFooter.vue'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: 'home',
    component: {
      header : App_header,
      default : main,
      footer : App_footer
    }
  },

]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router

