import { createRouter, createWebHistory } from 'vue-router'
import main from '../views/Main.vue'
import App_header from '../layout/AppHeader.vue'
import App_footer from '../layout/AppFooter.vue'

const routes = [
  {
    path: '/',
    name: 'components',
    component: {
      header : App_header,
      default : main,
      footer : App_footer
    }
  },
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
})
export default router
